mapper.select:
select * from T_BUSI_PMULTIPLEMSG


        MESGID=#{mesgid}

        AND NAVIGATIONID1=#{navigationid1}

        AND NAVIGATIONID2=#{navigationid2}

        AND MBANNER=#{mbanner}

        AND MMSGC=#{mmsgc}

        AND MTAGS=#{mtags}

        AND MTITLE=#{mtitle}

        AND MES=#{mes}

        AND ELLIPTICAL=#{elliptical}

        AND CREATETIME>=#{createtime}

        AND USERID=#{userid}

select IFNULL(COUNT(MESGID),0) CNT,IFNULL(MAX(MESGID),0) MAXID,IFNULL(MIN(MESGID),0) MINID from T_BUSI_PMULTIPLEMSG


        MESGID=#{mesgid}

        AND NAVIGATIONID1=#{navigationid1}

        AND NAVIGATIONID2=#{navigationid2}

        AND MBANNER=#{mbanner}

        AND MMSGC=#{mmsgc}

        AND MTAGS=#{mtags}

        AND MTITLE=#{mtitle}

        AND MES=#{mes}

        AND ELLIPTICAL=#{elliptical}

        AND CREATETIME>=#{createtime}

        AND USERID=#{userid}

select * from T_BUSI_PMULTIPLEMSG


        MESGID=#{mesgid}

        AND NAVIGATIONID1=#{navigationid1}

        AND NAVIGATIONID2=#{navigationid2}

        AND MBANNER=#{mbanner}

        AND MMSGC=#{mmsgc}

        AND MTAGS=#{mtags}

        AND MTITLE=#{mtitle}

        AND MES=#{mes}

        AND ELLIPTICAL=#{elliptical}

        AND CREATETIME>=#{createtime}

        AND USERID=#{userid}
 ORDER BY CREATETIME DESC

     LIMIT #{currentSize}, #{pageSize}

mapper.update:
update T_BUSI_PMULTIPLEMSG
NAVIGATIONID1=#{navigationid1}, NAVIGATIONID2=#{navigationid2}, MBANNER=#{mbanner}, MMSGC=#{mmsgc}, MTAGS=#{mtags}, MTITLE=#{mtitle}, MES=#{mes}, ELLIPTICAL=#{elliptical}, USERID=#{userid}, SAVETIME=NOW(),
where MESGID=#{mesgid} AND SAVETIME=#{savetime}

mapper.select:

SELECT MESGID,NAVIGATIONID1,NAVIGATIONID2,MTITLE
FROM T_BUSI_PMULTIPLEMSG
WHERE    MESGID IN(
SELECT MIN(MESGID) MESGID FROM T_BUSI_PMULTIPLEMSG    WHERE  NAVIGATIONID1=#{navigationid1} AND NAVIGATIONID2=#{navigationid2} AND MESGID>#{mesgid}
UNION ALL
SELECT MAX(MESGID) MESGID FROM T_BUSI_PMULTIPLEMSG    WHERE  NAVIGATIONID1=#{navigationid1} AND NAVIGATIONID2=#{navigationid2} AND MESGID<#{mesgid}
)
